/**
 * Enum that definitions horizontal alignment types.
 * @name HAlignment
 * @enum {string} HAlignment
 */

/**
 * Align to left.
 * value = 'left'
 * @constant
 * @memberof HAlignment
 */
export const LEFT = 'left';

/**
 * Align to center.
 * value = 'center'
 * @constant
 * @memberof HAlignment
 */
export const CENTER = 'center';

/**
 * Align to right.
 * value = 'right'
 * @constant
 * @memberof HAlignment
 */
export const RIGHT = 'right';
