/**
 * Enum the theme of components.
 * @name Theme
 * @enum {string} Theme
 */

/**
 * Align to light theme.
 * value = 'light'
 * @constant
 * @memberof Theme
 */
export const LIGHT = 'light';

/**
 * Align to dark theme.
 * value = 'dark'
 * @constant
 * @memberof Theme
 */
export const DARK = 'dark';

/**
 * Align to success theme.
 * value = 'success'
 * @constant
 * @memberof Theme
 */
export const SUCCESS = 'success';

/**
 * Align to warning theme.
 * value = 'warning'
 * @constant
 * @memberof Theme
 */
export const WARNING = 'warning';

/**
 * Align to error theme.
 * value = 'error'
 * @constant
 * @memberof Theme
 */
export const ERROR = 'error';

/**
 * Align to info theme.
 * value = 'info'
 * @constant
 * @memberof Theme
 */
export const INFO = 'info';
