/**
 * Enum that trigger the target element event.
 * @name Dock
 * @enum {string} Dock
 */

/**
 * Align to hover the target element.
 * value = 'hover'
 * @constant
 * @memberof Dock
 */
export const HOVER = 'hover';

/**
 * Align to click the target element.
 * value = 'click'
 * @constant
 * @memberof Dock
 */
export const CLICK = 'click';

/**
 * Align to focus/blur the target element.
 * value = 'focus'
 * @constant
 * @memberof Dock
 */
export const FOCUS = 'focus';
