/**
 * Enum that definitions vertical alignment types.
 * @name VAlignment
 * @enum {string} VAlignment
 */

/**
 * Align to top.
 * value = 'top'
 * @constant
 * @memberof VAlignment
 */
export const TOP = 'top';

/**
 * Align to middle.
 * value = 'middle'
 * @constant
 * @memberof VAlignment
 */
export const MIDDLE = 'middle';

/**
 * Align to bottom.
 * value = 'bottom'
 * @constant
 * @memberof VAlignment
 */
export const BOTTOM = 'bottom';
